import React, { useState, useEffect } from 'react';

const InteractiveMusicApp = () => {
  // 状態管理
  const [activeNote, setActiveNote] = useState(null);
  const [instrumentType, setInstrumentType] = useState('synth');
  const [visualEffect, setVisualEffect] = useState('circle');
  const [effects, setEffects] = useState([]);
  const [isAudioInitialized, setIsAudioInitialized] = useState(false);

  // オーディオの初期化
  useEffect(() => {
    const initAudio = async () => {
      try {
        // Toneをロード (Toneは既にimportされていると想定）
        const Tone = window.Tone;
        if (!Tone) {
          console.error('Tone.jsが利用できません');
          return;
        }

        await Tone.start();
        setIsAudioInitialized(true);
        console.log('オーディオ初期化完了');
      } catch (error) {
        console.error('オーディオの初期化に失敗しました:', error);
      }
    };

    initAudio();
  }, []);

  // 音を鳴らす
  const playNote = (note) => {
    try {
      const Tone = window.Tone;
      if (!Tone) return;

      // 一時的なシンセを作成して音を鳴らす
      const synth = new Tone.Synth().toDestination();
      synth.triggerAttackRelease(note, '0.5s');

      // メモリリーク防止
      setTimeout(() => synth.dispose(), 1000);

      // アクティブノートを設定
      setActiveNote(note);

      // 視覚エフェクトを作成
      createVisualEffect(note);

      // 1秒後にアクティブノートをリセット
      setTimeout(() => setActiveNote(null), 1000);
    } catch (error) {
      console.error('音の再生に失敗しました:', error);
    }
  };

  // ドラム音を鳴らす
  const playDrum = (drumType) => {
    try {
      const Tone = window.Tone;
      if (!Tone) return;

      let synth;

      switch (drumType) {
        case 'kick':
          synth = new Tone.MembraneSynth().toDestination();
          synth.triggerAttackRelease('C1', '0.1s');
          break;
        case 'snare':
          synth = new Tone.NoiseSynth().toDestination();
          synth.triggerAttackRelease('16n');
          break;
        case 'hihat':
          synth = new Tone.NoiseSynth({
            noise: { type: 'white' },
            envelope: { decay: 0.05 }
          }).toDestination();
          synth.triggerAttackRelease('32n');
          break;
        case 'cymbal':
          synth = new Tone.NoiseSynth({
            noise: { type: 'white' },
            envelope: { decay: 1.5 }
          }).toDestination();
          synth.triggerAttackRelease('8n');
          break;
        default:
          return;
      }

      // メモリリーク防止
      setTimeout(() => synth.dispose(), 2000);

      // アクティブノートを設定
      setActiveNote(drumType);

      // 視覚エフェクトを作成
      createVisualEffect(drumType);

      // 1秒後にアクティブノートをリセット
      setTimeout(() => setActiveNote(null), 1000);
    } catch (error) {
      console.error('ドラム音の再生に失敗しました:', error);
    }
  };

  // 視覚エフェクトを作成
  const createVisualEffect = (triggerName) => {
    // ランダムな値を生成
    const randomColor = '#' + Math.floor(Math.random()*16777215).toString(16);
    const randomSize = Math.floor(Math.random() * 100) + 50;
    const randomX = Math.floor(Math.random() * 80) + 10; // パーセントで指定
    const randomY = Math.floor(Math.random() * 80) + 10; // パーセントで指定

    // 新しいエフェクトを作成
    const newEffect = {
      id: Date.now(),
      note: triggerName,
      color: randomColor,
      size: randomSize,
      x: `${randomX}%`,
      y: `${randomY}%`,
      type: visualEffect
    };

    // エフェクトを追加
    setEffects(prev => [...prev, newEffect]);

    // 1秒後にエフェクトを削除
    setTimeout(() => {
      setEffects(prev => prev.filter(effect => effect.id !== newEffect.id));
    }, 1000);
  };

  // 音階の配列
  const notes = ['C4', 'D4', 'E4', 'F4', 'G4', 'A4', 'B4', 'C5'];

  return (
    <div className="flex flex-col items-center justify-center w-full h-full bg-gradient-to-b from-blue-900 to-purple-900 p-4">
      <h1 className="text-3xl font-bold text-white mb-6">インタラクティブ音楽アプリ</h1>

      {/* コントロールパネル */}
      <div className="mb-8 p-4 bg-white bg-opacity-20 rounded-lg">
        <div className="flex space-x-4">
          <div>
            <label className="block text-white mb-2">エフェクト:</label>
            <select 
              value={visualEffect} 
              onChange={(e) => setVisualEffect(e.target.value)}
              className="px-2 py-1 rounded bg-purple-700 text-white"
            >
              <option value="circle">円</option>
              <option value="square">四角</option>
              <option value="triangle">三角</option>
            </select>
          </div>
        </div>
      </div>

      {/* 鍵盤 */}
      <div className="flex space-x-2 mb-8">
        {notes.map((note) => (
          <button
            key={note}
            className={`w-12 h-32 ${
              note.includes('#') 
                ? 'bg-black text-white' 
                : 'bg-white text-black'
            } rounded-b-lg shadow-lg ${
              activeNote === note ? 'border-4 border-yellow-300' : ''
            }`}
            onClick={() => playNote(note)}
          >
            {note}
          </button>
        ))}
      </div>

      {/* ドラムパッド */}
      <div className="grid grid-cols-2 gap-4 mb-8">
        <button
          className={`w-24 h-24 bg-red-500 rounded-lg shadow-lg hover:bg-red-600 active:bg-red-700 ${activeNote === 'kick' ? 'ring-4 ring-yellow-300' : ''}`}
          onClick={() => playDrum('kick')}
        >
          キック
        </button>
        <button
          className={`w-24 h-24 bg-blue-500 rounded-lg shadow-lg hover:bg-blue-600 active:bg-blue-700 ${activeNote === 'snare' ? 'ring-4 ring-yellow-300' : ''}`}
          onClick={() => playDrum('snare')}
        >
          スネア
        </button>
        <button
          className={`w-24 h-24 bg-green-500 rounded-lg shadow-lg hover:bg-green-600 active:bg-green-700 ${activeNote === 'hihat' ? 'ring-4 ring-yellow-300' : ''}`}
          onClick={() => playDrum('hihat')}
        >
          ハイハット
        </button>
        <button
          className={`w-24 h-24 bg-yellow-500 rounded-lg shadow-lg hover:bg-yellow-600 active:bg-yellow-700 ${activeNote === 'cymbal' ? 'ring-4 ring-yellow-300' : ''}`}
          onClick={() => playDrum('cymbal')}
        >
          シンバル
        </button>
      </div>

      {/* 視覚エフェクト表示エリア */}
      <div className="relative w-full h-64 bg-black bg-opacity-30 rounded-lg overflow-hidden mb-4 border border-white border-opacity-20">
        <div className="absolute top-2 left-2 text-white text-opacity-60 text-sm">エフェクト表示エリア</div>

        {effects.map((effect) => {
          if (effect.type === 'circle') {
            return (
              <div
                key={effect.id}
                className="absolute rounded-full animate-ping"
                style={{
                  backgroundColor: effect.color,
                  width: effect.size,
                  height: effect.size,
                  left: effect.x,
                  top: effect.y,
                  opacity: 0.7
                }}
              />
            );
          } else if (effect.type === 'square') {
            return (
              <div
                key={effect.id}
                className="absolute animate-pulse"
                style={{
                  backgroundColor: effect.color,
                  width: effect.size,
                  height: effect.size,
                  left: effect.x,
                  top: effect.y,
                  opacity: 0.7,
                  transform: 'rotate(45deg)'
                }}
              />
            );
          } else if (effect.type === 'triangle') {
            return (
              <div
                key={effect.id}
                className="absolute animate-bounce"
                style={{
                  width: 0,
                  height: 0,
                  left: effect.x,
                  top: effect.y,
                  borderLeft: `${effect.size / 2}px solid transparent`,
                  borderRight: `${effect.size / 2}px solid transparent`,
                  borderBottom: `${effect.size}px solid ${effect.color}`,
                  opacity: 0.7
                }}
              />
            );
          }

          return null;
        })}
      </div>

      <p className="text-white text-center">画面の要素をクリックまたはタップして音を鳴らしてみましょう！</p>
    </div>
  );
};

export default InteractiveMusicApp;
